import { useEffect, useState } from "react";
import { Formik } from "formik";
export default function CreateTodo({ data, email }) {
  const [newtodo, setNewTodo] = useState([]);
  const [d, setD] = useState();
  let todoarr = [];
  const SubmitClicked = (values) => {
    todoarr = newtodo;

    setD(values.todo);
    let datatodo = {
      email: email,
      data: values.todo,
      date: new Date(),
      status: "Pending",
    };
    todoarr.push(datatodo);
    console.log(todoarr, "todoarr");
    setNewTodo(todoarr);
  };

  useEffect(() => {
    setNewTodo(data);
  }, []);

  return (
    <div>
      <div className="inputtodo-div">
        <div>
          <Formik
            initialValues={{ todo: "" }}
            onSubmit={(values) => {
              SubmitClicked(values);
            }}
          >
            {({
              values,
              errors,
              touched,
              handleChange,
              handleBlur,
              handleSubmit,
              isSubmitting,
              /* and other goodies */
            }) => (
              <form onSubmit={handleSubmit}>
                <br />
                <input
                  type="text"
                  name="todo"
                  onChange={handleChange}
                  required
                  onBlur={handleBlur}
                  value={values.todo}
                />
                <br />

                <button type="submit">+</button>
              </form>
            )}
          </Formik>
        </div>
      </div>
      <div>
        <p>{d}</p>
        {newtodo.length <= 0 ? (
          <h1>NO DATA</h1>
        ) : (
          newtodo.map((data, index) => {
            return (
              <div key={index}>
                <button>{data.data}</button>
              </div>
            );
          })
        )}
      </div>
    </div>
  );
}
