import { useEffect, useState } from "react";
import { Formik } from "formik";
export default function CreateTodo({ data, email }) {
  const [newtodo, setNewTodo] = useState([]);
  let todoarr = [];
  const SubmitClicked = (values) => {
    todoarr = newtodo;
    let datatodo = {
      email: email,
      data: values.data,
      date: new Date(),
      status: "Pending",
    };
    todoarr.append(datatodo);
    setNewTodo(todoarr);
  };

  useEffect(() => {
    setNewTodo(data);
  }, []);

  return (
    <div>
      <div className="inputtodo-div">
        <div>
          <Formik
            initialValues={{ todo: "" }}
            onSubmit={(values) => {
              SubmitClicked(values);
            }}
          >
            {({
              values,
              errors,
              touched,
              handleChange,
              handleBlur,
              handleSubmit,
              isSubmitting,
              /* and other goodies */
            }) => (
              <form onSubmit={handleSubmit}>
                <br />
                <input
                  type="text"
                  name="todo"
                  onChange={handleChange}
                  required
                  onBlur={handleBlur}
                  value={values.todo}
                />
                <br />

                <button type="submit">+</button>
              </form>
            )}
          </Formik>
        </div>
      </div>
    </div>
  );
}
