import { useEffect, useState } from "react";
import { Formik } from "formik";
import axios from "axios";
import { baseUrl } from "../api";
export default function CreateTodo({ data, email }) {
  const [newtodo, setNewTodo] = useState([]);
  let todoarr = [];
  const SubmitClicked = async (values) => {
    todoarr = [...newtodo];

    let datatodo = {
      email: email,
      data: values.todo,
      date: new Date(),
      status: "Pending",
    };
    await axios.post(`${baseUrl}/posttodo`, datatodo).then((res) => {
      console.log(res);
    });

    todoarr.push(datatodo);
    setNewTodo(todoarr);
  };

  useEffect(() => {
    console.log(dat);
    setNewTodo(data);
  }, [newtodo]);

  return (
    <div>
      <div className="inputtodo-div">
        <div>
          <Formik
            initialValues={{ todo: "" }}
            onSubmit={(values) => {
              SubmitClicked(values);
            }}
          >
            {({
              values,
              errors,
              touched,
              handleChange,
              handleBlur,
              handleSubmit,
              isSubmitting,
              /* and other goodies */
            }) => (
              <form onSubmit={handleSubmit}>
                <br />
                <input
                  type="text"
                  name="todo"
                  onChange={handleChange}
                  required
                  onBlur={handleBlur}
                  value={values.todo}
                />
                <br />

                <button type="submit">+</button>
              </form>
            )}
          </Formik>
        </div>
      </div>
      <div className="tododiv">
        {newtodo.length <= 0 ? (
          <h1>NO DATA</h1>
        ) : (
          newtodo.map((data, index) => {
            return (
              <div className="todolist" key={index}>
                <button className="datatodo">{data.data}</button>
                <button className="delete">X</button>
              </div>
            );
          })
        )}
      </div>
    </div>
  );
}
